//Select tile from the Tiles grid
tilesetContainer.addEventListener("mousedown", (event) => {
   selection = getCoords(event);
   tilesetSelection.style.left = selection[0] * 32 + "px";
   tilesetSelection.style.top = selection[1] * 32 + "px";
});

//Handler for placing new tiles on the map
function addTile(mouseEvent) {
   var clicked = getCoords(event);
   var key = clicked[0] + "-" + clicked[1];
   //console.log("clicked0:", clicked[0]) //testing
   //console.log("clicked1:", clicked[1]) //testing
   if (document.querySelector('.sliderline').className.match('slider sliderline on') && (lineclicked[0]!==0) && (lineclicked[1]!==0)) {
      if (Math.abs(lineclicked[0]-clicked[0])> Math.abs(lineclicked[1]-clicked[1])){
         console.log("horizontal");
         if (clicked[0] > lineclicked[0]) {
            for (let i = lineclicked[0]; i <= clicked[0]; i++) {
               key = i + "-" + clicked[1];
               addTile2(key, mouseEvent);
               lineclicked = [0, 0];
            }
         } else {
            for (let i = lineclicked[0]; i >= clicked[0]; i--) {
               key = i + "-" + clicked[1];
               addTile2(key, mouseEvent);
               lineclicked = [0, 0];
            }
         }
      } else {
         console.log("lineclicked:", lineclicked[0], lineclicked[1])
         console.log("clicked:", clicked[0], clicked[1])
         console.log("vertical")
         if (clicked[1] > lineclicked[1]){
            for (let i = lineclicked[1]; i <= clicked[1]; i++) {
               key = clicked[0] + "-" + i;
               addTile2(key, mouseEvent);
               lineclicked = [0, 0];
             }
         } else {
            for (let i = lineclicked[1]; i >= clicked[1]; i--) {
               key = clicked[0] + "-" + i;
               addTile2(key, mouseEvent);
               lineclicked = [0, 0];
             }
         }
      }
      lineclicked = [0, 0];
   } else {
      if (mouseEvent.shiftKey) {
         delete layers[currentLayer][key];
      } else {
         //console.log(selection[0]) //testing
         //console.log(selection[1]) //testing
         layers[currentLayer][key] = [selection[0], selection[1]];
         lineclicked = [clicked[0], clicked[1]];
      }
   }
   draw();
}

function addTile2(key,mouseEvent){
   layers[currentLayer][key] = [selection[0], selection[1]];
   draw();
}

//Bind mouse events for painting (or removing) tiles on click/drag
canvas.addEventListener("mousedown", () => {
   isMouseDown = true;
});
canvas.addEventListener("mouseup", () => {
   isMouseDown = false;
});
canvas.addEventListener("mouseleave", () => {
   isMouseDown = false;
});
canvas.addEventListener("mousedown", addTile);
canvas.addEventListener("mousemove", (event) => {
   if (isMouseDown) {
      addTile(event);
   }
});

//Utility for getting coordinates of mouse click
function getCoords(e) {
   const { x, y } = e.target.getBoundingClientRect();
   const mouseX = e.clientX - x;
   const mouseY = e.clientY - y;
   //this is for the mouse events based on the tile size, if you dont match the number with tile crop size,
   //your mouse click will not be accurate. KEEP IN MIND WHEN CHANGING DESIGN
   return [Math.floor(mouseX / 32), Math.floor(mouseY / 32)];
}

//converts data to image:data string and pipes into new browser tab
function exportImage() {
   var data = canvas.toDataURL();
   var image = new Image();
   image.src = data;

   var w = window.open("");
   w.document.write(image.outerHTML);
}

//Reset state to empty
function clearCanvas() {
   layers = [{}, {}, {}];
   draw();
}

function setLayer(newLayer) {
   //Update the layer
   currentLayer = newLayer;
   //Update the UI to show updated layer
   var oldActiveLayer = document.querySelector(".layer.active");
   var oldActiveIcon = document.querySelector(".pen-icon.active");
   if (oldActiveLayer) {
      oldActiveLayer.classList.remove("active");
      oldActiveIcon.classList.remove("active");
   }
   document.querySelector(`[tile-layer="${currentLayer}"]`).classList.add("active");
   document.querySelector(`[tile1-layer="${currentLayer}"]`).classList.add("active");
}

function draw() {
   var ctx = canvas.getContext("2d");
   ctx.clearRect(0, 0, canvas.width, canvas.height);

   //Size of each crop from the tileset. change canvas dimensions if you want different size. 
   //do NOT change tile crop.
   var size_of_crop = 32; 
   
   layers.forEach((layer) => {
      Object.keys(layer).forEach((key) => {
         //Determine x/y position of this placement from key ("3-4" -> x=3, y=4)
         var positionX = Number(key.split("-")[0]);
         var positionY = Number(key.split("-")[1]);
         var [tilesheetX, tilesheetY] = layer[key]; 

         ctx.drawImage(
            tilesetImage,
            tilesheetX * 32,//YOU CAN CHANGE THESE FOR THE SIZE OF TILESHEET, do not change the positions.
            tilesheetY * 32,
            size_of_crop,
            size_of_crop,
            positionX * 32, // positions need to match <size_of_crop> variable.
            positionY * 32,
            size_of_crop,
            size_of_crop
         );
      });
   });
}


function sliders_on() {
   if (!slider3.classList.contains("on")){
      layers[2] = topLayer;
      topLayer={};
      slider3.classList.add("on");
   }
   if (!slider2.classList.contains("on")){
      layers[1] = middleLayer;
      middleLayer={};
      slider2.classList.add("on");
   }
   if (!slider1.classList.contains("on")){
      layers[0] = bottomLayer;
      bottomLayer={};
      slider1.classList.add("on");
   }
}

